import{_ as l,o as e,c as a,x as i}from"./chunks/framework.f6ec665f.js";const m=JSON.parse('{"title":"git的一些常用命令","description":"git的一些常用命令，搜集整理，开始轻松使用git","frontmatter":{"title":"git的一些常用命令","display":"home","description":"git的一些常用命令，搜集整理，开始轻松使用git","image":"https://picsum.photos/536/354?random&date=2019-08-18","date":"2019-08-18T00:00:00.000Z","vssue-title":"git","tags":["git"],"categories":["learn"]},"headers":[],"relativePath":"posts/git.md","filePath":"posts/git.md","lastUpdated":1692427508000}'),o={name:"posts/git.md"},t=i(`<p>搜集整理一些 <code>git</code> 的常用命令</p><h2 id="配置用户" tabindex="-1">配置用户 <a class="header-anchor" href="#配置用户" aria-label="Permalink to &quot;配置用户&quot;">​</a></h2><ul><li><code>git config --global user.name &#39;your name&#39;</code></li></ul><p>设置全局的用户名</p><ul><li><code>git config --global user.email &#39;your email&#39;</code></li></ul><p>设置全局的邮箱</p><h2 id="创建仓库" tabindex="-1">创建仓库 <a class="header-anchor" href="#创建仓库" aria-label="Permalink to &quot;创建仓库&quot;">​</a></h2><ul><li><code>git init</code></li></ul><p>将当前的文件夹初始化为一个 git 仓库</p><ul><li><code>git clone ...</code></li></ul><p>克隆一个仓库</p><h2 id="暂存区相关" tabindex="-1">暂存区相关 <a class="header-anchor" href="#暂存区相关" aria-label="Permalink to &quot;暂存区相关&quot;">​</a></h2><ul><li><code>git status</code></li></ul><p>查看当前工作区和暂存区文件的状态</p><ul><li><code>git add ...</code></li></ul><p>增加文件到缓存区，通过 <code>git add *</code> 增加所有</p><ul><li><code>git checkout -- ...</code></li></ul><p>丢弃工作区的文件修改</p><ul><li><code>git rm ...</code></li></ul><p>将文件从版本库中删除</p><ul><li><code>git commit -m &#39;...&#39;</code></li></ul><p>创建提交信息</p><ul><li><code>git reset</code></li></ul><p>把暂存区的修改回退到工作区</p><ul><li><code>git reset --hard [commit_id]</code></li></ul><p>版本回退，使用 <code>git log</code> 查看 <strong>commit_id</strong></p><h2 id="分支相关" tabindex="-1">分支相关 <a class="header-anchor" href="#分支相关" aria-label="Permalink to &quot;分支相关&quot;">​</a></h2><ul><li><code>git branch</code></li></ul><p>查看当前分支</p><ul><li><code>git checkout -b ...</code></li></ul><p>创建分支...</p><ul><li><code>git branch -d ...</code></li></ul><p>删除分支...</p><ul><li><code>git checkout ...</code></li></ul><p>切换到...分支</p><ul><li><code>git rebase ...</code></li></ul><p>将...分支的内容合并到当前分支上</p><h2 id="标签" tabindex="-1">标签 <a class="header-anchor" href="#标签" aria-label="Permalink to &quot;标签&quot;">​</a></h2><ul><li><code>git tag ...</code></li></ul><p>查看所有标签</p><ul><li><code>git tag ...</code></li></ul><p>创建标签</p><ul><li><code>git tag -d ...</code></li></ul><p>删除标签</p><h2 id="推送改动到远程" tabindex="-1">推送改动到远程 <a class="header-anchor" href="#推送改动到远程" aria-label="Permalink to &quot;推送改动到远程&quot;">​</a></h2><ul><li><code>git remote add origin ...</code></li></ul><p>增加远程仓库</p><ul><li><code>git push origin ...</code></li></ul><p>提交改动到远端仓库，使用 <code>git push origin master</code> 推送到远程主分支</p><h2 id="更新与合并" tabindex="-1">更新与合并 <a class="header-anchor" href="#更新与合并" aria-label="Permalink to &quot;更新与合并&quot;">​</a></h2><ul><li><code>git pull</code></li></ul><p>更新本地仓库至最新改动</p><ul><li><code>git merge ...</code></li></ul><p>将...分支的内容合并到当前分支上</p><h2 id="配置别名" tabindex="-1">配置别名 <a class="header-anchor" href="#配置别名" aria-label="Permalink to &quot;配置别名&quot;">​</a></h2><ul><li><code>git config --global alias.[...] ...</code></li></ul><p>为指令配置别名</p><h5 id="例如" tabindex="-1">例如 <a class="header-anchor" href="#例如" aria-label="Permalink to &quot;例如&quot;">​</a></h5><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">git</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">config</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--global</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">alias.st</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">status</span></span>
<span class="line"></span>
<span class="line"><span style="color:#FFCB6B;">git</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">st</span></span></code></pre></div>`,59),c=[t];function s(d,p,n,r,u,g){return e(),a("div",null,c)}const _=l(o,[["render",s]]);export{m as __pageData,_ as default};
